var ot_create_Ogs = Class.create();
ot_create_Ogs.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getOrgObj: function () {
        var sn = new sn_ws.RESTMessageV2();
        sn.setEndpoint("https://m0ayqnd8l9.execute-api.us-east-1.amazonaws.com/Test");
        sn.setHttpMethod("GET");
        sn.setRequestHeader("APIKey", "ccf1be5d04b7f475ec2455ab825aed1a");
sn.setRequestHeader("user-agent",'Mozilla/5.0 (Macintosh; Intel Mac OS X 10_13_6) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/67.0.3396.99 Safari/537.36');
        sn.setRequestHeader("Accept","application/json");
         sn.setRequestHeader('Content-Type','application/json');
        var response = sn.executeAsync();
        response.waitForResponse(100);
        httpResponseStatus = response.getStatusCode();
        var responseBody = JSON.parse(response.getBody());
        this.flattenOrg(responseBody);
    },

    flattenOrg: function(obj) {
        var gr = new GlideRecord('x_261942_ot_orgtable');
        gr.newRecord();
        gr.parent_id = obj.parentId;
        gr.org_name = obj.name;
        gr.position = obj.position;
        gr.ot_id = obj.id;
//         gr.addQuery('ot_id', obj.parentId);
//         gr.query();
//         gr.u_parent = gr.name;
        gr.insert();
        if (obj.children && obj.children.length) {
            for (var i = 0; i < obj.children.length; i++) {
                this.flattenOrg(obj.children[i]);
            }
        }

    },

    type: 'ot_create_Ogs'
});
